const crypto = require('crypto');

// Filtere Trends nach Nachhaltigkeit Keywords
const sustainabilityKeywords = [
  'nachhaltigkeit', 'sustainability', 'klimaschutz', 'umwelt',
  'erneuerbare energie', 'solar', 'wind', 'elektro',
  'recycling', 'bio', 'öko', 'green', 'klimawandel',
  'elektroauto', 'vegan', 'fairtrade', 'co2', 'emission'
];

const trendsData = JSON.parse($input.all()[0].json.body.substring(5)); // Remove )]}' prefix
const relevantTrends = [];

for (const day of trendsData.default.trendingSearchesDays) {
  for (const search of day.trendingSearches) {
    const title = search.title.query.toLowerCase();
    const isRelevant = sustainabilityKeywords.some(keyword => 
      title.includes(keyword)
    );

    if (isRelevant) {
      // Erstelle eindeutigen Hash für diesen Trend
      const trendContent = JSON.stringify({
        query: search.title.query,
        date: day.date,
        traffic: search.formattedTraffic
      });

      const trendHash = crypto.createHash('sha256')
        .update(trendContent)
        .digest('hex');

      relevantTrends.push({
        query: search.title.query,
        traffic: search.formattedTraffic,
        articles: search.articles || [],
        date: day.date,
        trendHash: trendHash,
        sourceData: search
      });
    }
  }
}

return relevantTrends.map(trend => ({ json: trend }));

